package firstWeek;

public class IfMoon2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 120;

		if (num < 0) {
			System.out.println("0 미만");
		} else if (num < 100) {
			System.out.println(	"0 이상 100 미만");
		} else {
			System.out.println("100 이상");
		}
	}
}
